# init_database.py import logging from forest_app.persistence import init_db # Import the init_db function # Configure basic logging logging.basicConfig(level=logging.INFO) logger = logging.getLogger(__name__) if __name__ == "__main__": logger.info("Attempting to initialize the database...") try: init_db() # Call the function to create tableslogger.info("Database initialized successfully.") logger.info("The 'forest.db' file should now exist with the necessary tables.") except Exception as e: logger.exception("An error occurred during database initialization: %s", e)
